  transition: all 0.4s ease;
}
.button:hover::after {
  transform: scale(1.05, 1.1);
  box-shadow: inset 0 -1px 3px 0 rgba(255, 255, 255, 1);
}
.button::before {
  content: "";
  inset: 7px 6px 6px 6px;
  position: absolute;
  background: linear-gradient(to top, var(--neutral-1), var(--neutral-2));
  border-radius: 30px;
  filter: blur(0.5px);
  z-index: 2;
}
.state p {
  display: flex;
  align-items: center;
  justify-content: center;
}
.state .icon {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  margin: auto;
  transform: scale(1.25);
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}
.state .icon svg {
  overflow: visible;
}

/* Outline */
.outline {
  position: absolute;
  border-radius: inherit;
  overflow: hidden;
  z-index: 1;
  opacity: 0;
  transition: opacity 0.4s ease;
  inset: -2px -3.5px;
}
.outline::before {
  content: "";
  position: absolute;
  inset: -100%;
  background: conic-gradient(
    from 180deg,
    transparent 60%,
    white 80%,
    transparent 100%
  );
  animation: spin 2s linear infinite;
  animation-play-state: paused;
}
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.button:hover .outline {
  opacity: 1;
}
.button:hover .outline::before {
  animation-play-state: running;
}

/* Letters */
.state p span {
  display: block;
  opacity: 0;
  animation: slideDown 0.8s ease forwards calc(var(--i) * 0.03s);
}
.button:hover p span {
  opacity: 1;
  animation: wave 0.5s ease forwards calc(var(--i) * 0.02s);
}
.button:focus p span {
  opacity: 1;
  animation: disapear 0.6s ease forwards calc(var(--i) * 0.03s);
}
@keyframes wave {
  30% {
    opacity: 1;
    transform: translateY(4px) translateX(0) rotate(0);
  }
  50% {
    opacity: 1;
    transform: translateY(-3px) translateX(0) rotate(0);
    color: var(--primary);
  }
  100% {
    opacity: 1;
    transform: translateY(0) translateX(0) rotate(0);
  }
}
@keyframes slideDown {
  0% {
    opacity: 0;
    transform: translateY(-20px) translateX(5px) rotate(-90deg);
    color: var(--primary);
    filter: blur(5px);
  }
  30% {
    opacity: 1;
    transform: translateY(4px) translateX(0) rotate(0);
    filter: blur(0);
  }
  50% {
    opacity: 1;
    transform: translateY(-3px) translateX(0) rotate(0);
  }
  100% {
    opacity: 1;
    transform: translateY(0) translateX(0) rotate(0);
  }
}
@keyframes disapear {
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
    transform: translateX(5px) translateY(20px);
    color: var(--primary);
    filter: blur(5px);
  }
}

/* Plane */
.state--default .icon svg {
  animation: land 0.6s ease forwards;
}
.button:hover .state--default .icon {
  transform: rotate(45deg) scale(1.25);
}
.button:focus .state--default svg {
  animation: takeOff 0.8s linear forwards;
}
.button:focus .state--default .icon {
  transform: rotate(0) scale(1.25);
}
@keyframes takeOff {
  0% {
    opacity: 1;
  }
  60% {
    opacity: 1;
    transform: translateX(70px) rotate(45deg) scale(2);
  }
  100% {
    opacity: 0;
    transform: translateX(160px) rotate(45deg) scale(0);
  }
}
@keyframes land {
  0% {
    transform: translateX(-60px) translateY(30px) rotate(-50deg) scale(2);
    opacity: 0;
    filter: blur(3px);
  }
  100% {
    transform: translateX(0) translateY(0) rotate(0);
    opacity: 1;
    filter: blur(0);
  }
}

/* Contrail */
.state--default .icon:before {
  content: "";
  position: absolute;
  top: 50%;
  height: 2px;
  width: 0;
  left: -5px;
  background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.5));
}
.button:focus .state--default .icon:before {
  animation: contrail 0.8s linear forwards;
}
@keyframes contrail {
  0% {
    width: 0;
    opacity: 1;
  }
  8% {
    width: 15px;
  }
  60% {
    opacity: 0.7;
    width: 80px;
  }
  100% {
    opacity: 0;
    width: 160px;
  }
}

/* States */
.state {
  padding-left: 29px;
  z-index: 2;
  display: flex;
  position: relative;
}
.state--default span:nth-child(4) {
  margin-right: 5px;
}
.state--sent {
  display: none;
}
.state--sent svg {
  transform: scale(1.25);
  margin-right: 8px;
}
.button:focus .state--default {
  position: absolute;
}
.button:focus .state--sent {
  display: flex;
}
.button:focus .state--sent span {
  opacity: 0;
  animation: slideDown 0.8s ease forwards calc(var(--i) * 0.2s);
}
.button:focus .state--sent .icon svg {
  opacity: 0;
  animation: appear 1.2s ease forwards 0.8s;
}
@keyframes appear {
  0% {
    opacity: 0;
    transform: scale(4) rotate(-40deg);
    color: var(--primary);
    filter: blur(4px);
  }
  30% {
    opacity: 1;
    transform: scale(0.6);
    filter: blur(1px);
  }
  50% {
    opacity: 1;
    transform: scale(1.2);
    filter: blur(0);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

/* From Uiverse.io by marcelodolza */ 
.button-wrap {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: radial-gradient(
      rgba(255, 255, 255, 0.02) 1px,
      transparent 3px
    ),
    radial-gradient(circle at center, #11161f 0%, #08090b 100%);
  background-size:
    7px 7px,
    100% 100%;
  background-position:
    0 0,
    center center;
}

.button {
  --radius: 18px;
  outline: none;
  cursor: pointer;
  font-size: 22px;
  background: transparent;
  border: 0;
  position: relative;
  width: 228px;
  height: 64px;
  box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.6);
  border-radius: var(--radius);
}
.button::before,
.button::after {
  content: "";
  position: absolute;
  top: 40%;
  z-index: 0;
  height: 50px;
  width: 50%;
  background: black;
  filter: blur(10px);
  border-radius: 10px;
  opacity: 0.7;
  pointer-events: none;
  touch-action: none;
}
.button::before {
  left: -2px;
  transform: rotate(-15deg);
}
.button::after {
  right: -2px;
  transform: rotate(15deg);
}

.glow {
  position: absolute;
  inset: 0;
  box-shadow: 0 0 200px 5px rgba(255, 208, 0, 0.4);
  pointer-events: none;
  touch-action: none;
}

.glow::before {
  content: "";
  position: absolute;
  margin: auto;
  background: rgba(160, 158, 122, 0.05);
  border-radius: 50%;
  filter: blur(50px);
  inset: 0;
  height: 500px;
  width: 150px;
  transform: rotate(-45deg);
}

.wave {
  position: absolute;
  width: 100%;
  height: 100%;
  inset: 0;
  margin: auto;
  transition: all 0.3s linear;
}
.wave::before,
.wave::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  border: 0.5px solid rgba(255, 208, 0, 0.4);
  inset: 0;
  filter: blur(2px);
  border-radius: 30px;
}
.button:focus .wave::before,
.button:focus .wave::after {
  animation: wave 2.2s linear;
}
.button:focus .wave::after {
  animation-delay: 0.35s;
}
@keyframes wave {
  0%,
  40% {
    transform: scale(1);
    opacity: 0;
    box-shadow:
      0 0 30px 10px black,
      inset 0 0 30px rgba(255, 255, 255, 0.1);
  }
  60% {
    transform: scale(1.2, 1.5);
    opacity: 1;
  }
  100% {
    transform: scale(1.6, 2);
    opacity: 0;
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.1);
  }
}

.bg {
  position: absolute;
  inset: -7px;
  border-radius: calc(var(--radius) * 1.35);
  z-index: 1;
  overflow: hidden;
  box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.6);
}
.bg::before,
.bg::after {
  content: "";
  position: absolute;
  inset: 0;
  border-radius: calc(var(--radius) * 1.35);
}
.bg::after {
  background: black;
  box-shadow:
    inset 0 0 0 1px rgba(92, 83, 54, 0.4),
    inset -2px 0 0 -1px rgba(92, 83, 54, 1),
    inset 0 -2px 0 -1px rgba(92, 83, 54, 0.1);
}

.bg .shine {
  transition: all 0.5s linear;
  color: rgba(255, 239, 168, 0.85);
}
.button:active .bg .shine {
  color: rgba(42, 255, 205, 0.85);
}
.bg .shine::before {
  content: "";
  position: absolute;
  z-index: 2;
  background: currentColor;
  width: 10px;
  height: 10px;
  opacity: 0.3;
  left: 0;
  right: 0;
  bottom: 0;
  margin: auto;
  border-radius: 50%;
  filter: blur(2px);
  transform: translateY(0) scale(0);
  animation: shine 2.2s linear infinite;
}
@keyframes shine {
  0% {
    transform: translateY(0) scale(0);
  }
  20% {
    transform: translateY(0) scale(25);
  }
  100% {
    transform: translateY(-280px) scale(20, 18);
  }
}

.outline {
  position: absolute;
  overflow: hidden;
  inset: 0;
  outline: none;
  border-radius: inherit;
  transition: all 0.4s ease;
}
.outline::before {
  content: "";
  position: absolute;
  inset: 0;
  width: 120px;
  height: 300px;
  margin: auto;
  background: linear-gradient(
    to right,
    transparent 0%,
    #ffea00 50%,
    transparent 100%
  );
  animation: spin 1.7s linear infinite;
}
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.circuit {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%);
  width: 392px;
  height: auto;
  margin-top: -13px;
  pointer-events: none;
  touch-action: none;
}
.circuit-bg {
  opacity: 0.5;
}

.circuit-path {
  opacity: 0;
  transition: opacity 0.4s linear;
}

.circuit-path .circuit-side path {
  stroke-dasharray: 200;
  stroke-dashoffset: 200;
  animation: line-1 1.7s ease-in-out infinite;
  animation-delay: calc(var(--i) * 0.2s);
}
@keyframes line-1 {
  0% {
    stroke-dashoffset: 200;
    filter: blur(2px) brightness(2);
  }
  70% {
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
  }
}
.circuit-path .circuit-bottom path {
  stroke-dasharray: 250;
  stroke-dashoffset: -250;
  animation: line-2 2.2s ease-in-out infinite;
  animation-delay: calc(var(--i) * 0.1s);
}
@keyframes line-2 {
  0% {
    stroke-dashoffset: -250;
    filter: blur(2px) brightness(2);
  }
  70% {
    stroke-dashoffset: 0;
  }
  100% {
    opacity: 0;
  }
}
.circuit-path-2 .circuit-side path {
  animation-duration: 0.7s;
}
.circuit-path-2 .circuit-bottom path {
  animation-duration: 0.9s;
}
.circuit-path-2 .circuit-side path,
.circuit-path-2 .circuit-bottom path {
  filter: sepia(50%) saturate(500%) hue-rotate(90deg) brightness(100%)
    contrast(100%) drop-shadow(0 0 0 white);
}
.button .circuit-path-1,
.button:hover .circuit-path-2 {
  opacity: 1;
}
.button:hover .circuit-path-1 {
  opacity: 0;
}
.button:focus .wrap:hover .circuit-path-1 {
  opacity: 1;
}
.button:focus .wrap:hover .circuit-path-2,
.button:active .wrap:hover .circuit-path-1 {
  opacity: 0;
}
.button:active .wrap:hover .circuit-path-2 {
  opacity: 1;
}

.wrap-content {
  position: absolute;
  inset: 0;
  z-index: 1;
  transition: all 0.3s ease;
}
.button:active .wrap-content {
  transform: scale(0.97, 0.96);
}
.content {
  border-radius: var(--radius);
  overflow: hidden;
  height: 100%;
  padding: 2px;
  background: #fedc83;
  box-shadow:
    inset 0 0 16px 6px #c88a00,
    inset 0 -10px 10px -8px #ffac82;
  position: relative;
}
.button:hover .content {
  animation: shake 0.5s linear infinite;
}
@keyframes shake {
  15% {
    transform: translate(1px, 1px);
  }
  30% {
    transform: translate(0, -1px);
  }
  45% {
    transform: translate(1px, -1px);
  }
  55% {
    transform: translate(-1px, 1px);
  }
  70% {
    transform: translate(1px, 0);
  }
  85% {
    transform: translate(-1px, -1px);
  }
}
.button:focus .content {
  animation: shake-out 1.9s ease forwards;
}
@keyframes shake-out {
  5% {
    transform: translate(2px, 2px);
  }
  10% {
    transform: translate(0, -2px);
  }
  15% {
    transform: translate(2px, -2px);
  }
  20% {
    transform: translate(-2px, 2px);
  }
  25% {
    transform: translate(2px, 0);
  }
  30% {
    transform: translate(-2px, -2px);
  }
  35% {
    transform: translate(2px, 2px);
  }
  40% {
    transform: translate(0, -2px);
  }
  45% {
    transform: translate(2px, -2px);
  }
  48% {
    transform: scale(0.9);
  }
  100% {
    transform: translate(0);
  }
}

.glyphs {
  pointer-events: none;
  touch-action: none;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1;
  position: relative;
  height: 100%;
  gap: 10px;
  border-radius: calc(var(--radius) * 0.85);
  font-weight: 600;
  background: #fedc83;
  box-shadow:
    inset 0 0 16px 6px #c88a00,
    0 0 10px 10px rgba(0, 0, 0, 0.2),
    inset 0 -10px 10px -8px rgb(255, 89, 0);
  transition: all 0.3s ease;
}

.text {
  transition: all 0.3s ease;
  transform: translateY(-1px);
  display: flex;
  align-items: center;
  justify-content: center;
}
.text span {
  display: block;
  color: transparent;
  position: relative;
}
.text span:nth-child(5) {
  margin-right: 5px;
}
.text span {
  animation: char-in 1.2s ease backwards calc(var(--i) * 0.03s);
}
.text span::before,
.text span::after {
  content: attr(data-label);
  position: absolute;
  color: black;
  text-shadow: 0 2px 3px #dabc45;
  left: 0;
}
.text span::before {
  opacity: 0;
  transform: translateY(-100%);
}
.button:hover .text span::before {
  animation: char-in 0.7s ease calc(var(--i) * 0.03s);
}
.button:hover .text span::after {
  opacity: 1;
  animation: char-out 0.7s ease calc(var(--i) * 0.03s) backwards;
}
.button:focus .text span {
  animation: char-out-2 0.7s ease forwards calc((var(--i)) * -0.02s);
}
@keyframes char-in {
  0% {
    opacity: 0;
    transform: translateY(60%);
    filter: blur(20px);
    color: blue;
  }
  30% {
    transform: translateY(-15%);
    opacity: 1;
    filter: blur(1px);
    color: #ff5100;
  }
  60% {
    transform: translateY(5%);
    opacity: 1;
    filter: blur(0);
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}
@keyframes char-out {
  0% {
    transform: translateY(0);
    opacity: 1;
  }
  100% {
    transform: translateY(-70%);
    opacity: 0;
    filter: blur(4px);
  }
}
@keyframes char-out-2 {
  0%,
  50% {
    transform: translateY(0);
    opacity: 1;
  }
  100% {
    transform: translateY(-70%);
    opacity: 0;
    filter: blur(4px);
  }
}

.icon-1 svg,
.icon-2 svg {
  margin-top: 3px;
}
.button .icon-2 {
  position: absolute;
  filter: blur(5px);
  opacity: 0;
  animation: icon-2-out 0.6s ease-in forwards;
}
@keyframes icon-2-out {
  0% {
    transform: translateX(0);
    filter: blur(0);
    opacity: 1;
  }
  100% {
    transform: translateX(100px);
    filter: blur(5px);
    opacity: 0;
  }
}
.button .icon-2 svg {
  height: 28px;
  width: auto;
  overflow: visible;
}
.button .icon-2 .cloud {
  animation: cloud 6s linear infinite 0.3s;
  opacity: 0;
  filter: blur(10px);
  animation-play-state: paused;
}
.button .icon-2 .cloud-2 {
  animation-delay: 1.5s;
}
.button:focus .icon-2 .cloud {
  animation-play-state: running;
}
@keyframes cloud {
  0%,
  20% {
    transform: translateX(-140px);
    opacity: 0;
  }
  70% {
    transform: translateX(0);
    opacity: 0.7;
  }
  100% {
    transform: translateX(140px);
    opacity: 0;
  }
}
.button:focus .icon-2 {
  animation: icon-2-in 1.5s cubic-bezier(0.5, -0.15, 0.3, 1) 0.8s forwards;
}
@keyframes icon-2-in {
  0% {
    transform: translateY(20px);
    filter: blur(5px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    filter: blur(0);
    opacity: 1;
  }
}
.button:focus .icon-2 .check {
  stroke-dasharray: 110;
  stroke-dashoffset: 110;
  animation: check 1.5s cubic-bezier(0.5, -0.15, 0.3, 1) forwards 1.2s;
}
@keyframes check {
  0% {
    stroke-dashoffset: 110;
    stroke-width: 5px;
  }
  100% {
    stroke-dashoffset: 0;
  }
}
.button .cloud-mask {
  animation: cloud-mask 2.2s ease infinite;
}
@keyframes cloud-mask {
  0%,
  5% {
    transform: translateX(0);
    opacity: 0.5;
  }
  65%,
  100% {
    transform: translateX(640px);
    opacity: 1;
  }
}
#cloud-mask-bg {
  color: #ffefa8;
}
.button:active #cloud-mask-bg {
  color: #90ffeb;
}

.icon-1 {
  margin-right: -8px;
  animation: icon-1-in 1s cubic-bezier(0.5, -0.15, 0.3, 1.4) forwards;
}
@keyframes icon-1-in {
  0% {
    transform: translateX(-230px) scale(2.5) rotate(-130deg);
    filter: blur(4px);
  }
  80% {
    transform: translateX(5px);
    filter: blur(3px);
    color: #ffd15b;
  }
  100% {
    transform: translate(0);
  }
}
.icon-1:active path:nth-child(1) {
  animation: none;
}
.button:focus .icon-1 {
  animation: icon-1-out 1.5s ease-in-out forwards;
}
@keyframes icon-1-out {
  0% {
    transform: translate(0);
  }
  40% {
    transform: translateX(-250px) scale(2) rotate(-90deg);
    filter: blur(5px);
  }
  40.0001% {
    transform: translate(-80px, -80px) scale(4);
    filter: contrast(2);
    opacity: 1;
    color: white;
    background-color: transparent;
    box-shadow: none;
  }
  70%,
  97% {
    transform: translate(-80px, 70px) scale(1);
    box-shadow: 0 0 50px 100px white;
    background-color: white;
    border-radius: 50%;
  }
  100% {
    opacity: 0;
  }
}
.icon-1 svg {
  height: 32px;
  width: auto;
  overflow: visible;
  fill: #3a3631;
}
.icon-1 .stroke {
  stroke-dasharray: 90;
  stroke-dashoffset: -90;
  filter: blur(2px);
  animation: icon-stroke 3s linear infinite;
}
@keyframes icon-stroke {
  0% {
    stroke-dashoffset: -90;
  }
  100% {
    stroke-dashoffset: 90;
  }
}
.icon-1 .stroke:nth-child(1) {
  stroke: #00ffff;
  stroke-width: 4px;
}
.icon-1 .stroke:nth-child(2) {
  stroke: #ff7700;
  stroke-width: 3px;
  animation-delay: 1.5s;
}
.icon-1 .lightnings > g {
  transform: translate(15px, -15px);
}
.icon-1 .lightning {
  transform-origin: center;
  animation: lightning-1 0.9s linear infinite;
}
.icon-1 .lightning:last-child {
  animation: lightning-1 1.1s linear infinite 0.2s;
}
.icon-1 .lightning-2 {
  transform-origin: center;
  animation: lightning-2 0.9s linear infinite 0.2s;
}
.icon-1 .lightning-2:last-child {
  animation: lightning-2 1.1s linear infinite 0.4s;
}
.icon-1 g {
  opacity: 0;
  transition: 0.4s linear;
}
.button:hover .icon-1 g {
  opacity: 1;
}
@keyframes lightning-1 {
  0% {
    transform: translate(30px, -30px);
    filter: blur(10px);
    opacity: 0;
    color: white;
  }
  50% {
    transform: translate(0);
    filter: blur(0);
    opacity: 1;
    color: #acacac;
  }
  100% {
    transform: translate(-30px, 30px);
    filter: blur(10px);
    opacity: 0;
    color: white;
  }
}
@keyframes lightning-2 {
  0% {
    transform: translate(30px, -30px);
    filter: blur(10px);
    opacity: 0;
  }
  50% {
    transform: translate(0);
    filter: blur(0);
    opacity: 1;
    color: white;
  }
