.container input {
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  opacity: 0;
  cursor: pointer;
  height: 0;
  width: 0;
}

.container {
  display: block;
  position: relative;
  cursor: pointer;
  font-size: 20px;
  user-select: none;
  border: 3px solid #beddd0;
  border-radius: 10px;
  overflow: hidden;
}

/* Create a custom checkbox */
.checkmark {
  position: relative;
  top: 0;
  left: 0;
  height: 1.3em;
  width: 1.3em;
  background-color: #2dc38c;
  border-bottom: 1.5px solid #2dc38c; /* Bottom stroke */
  box-shadow: 0 0 1px #cef1e4, inset 0 -2.5px 3px #62eab8,
    inset 0 3px 3px rgba(0, 0, 0, 0.34); /* Inner shadow */
  border-radius: 8px;
  transition: transform 0.3s ease-in-out; /* Transition for smooth animation */
}

/* When the checkbox is checked, modify the checkmark appearance */
.container input:checked ~ .checkmark {
  transform: translateY(40px); /* Move down */
  animation: wipeDown 0.6s ease-in-out forwards; /* Apply wipe animation */
}

/* When the checkbox is not checked, modify the checkmark appearance */
.container input:not(:checked) ~ .checkmark {
  transform: translateY(-40px); /* Move up */
  animation: wipeUp 0.6s ease-in-out forwards; /* Apply wipe animation */
}

/* Keyframes for wipe animations */
@keyframes wipeDown {
  0% {
    transform: translateY(0); /* Starting position */
  }
  100% {
    transform: translateY(40px); /* End position */
  }
}

@keyframes wipeUp {
  0% {
    transform: translateY(40); /* Starting position */
  }
  100% {
    transform: translateY(0px); /* End position */
  }
}

/* Create the checkmark/indicator */
.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}

/* Show the checkmark when checked */
.container input:checked ~ .checkmark:after {
  display: block;
}

/* Style the checkmark/indicator */
.container .checkmark:before {
  content: "";
  position: absolute;
  left: 10px;
  top: 4px;
  width: 5px;
  height: 10px;
  border: solid white;
  border-width: 0 2px 2px 0;
  transform: rotate(45deg);
  box-shadow: 0 4px 2px rgba(0, 0, 0, 0.34); /* Icon drop shadow */
}

/* From Uiverse.io by marcelodolza */ 
.output {
  align-self: center;
  background: inherit;
  border-radius: 100px;
  padding: 0 12px 0 10px;
  height: 36px;
  min-width: 350px;
  position: relative;
  top: -140px;

  .cover {
    position: absolute;
    top: 2px;
    right: 2px;
    bottom: 2px;
    left: 2px;
    border-radius: 100px;
    clip-path: inset(0 0 0 0 round 100px);
    background: #101216;
    transition: filter 1000ms cubic-bezier(0, 0, 0, 1);
    filter: blur(5px);
  }
  .cover::after {
    content: "";
    top: -10px;
    right: -10px;
    bottom: -10px;
    left: -10px;
    border-radius: 100px;
    position: absolute;
    background: inherit;
    opacity: 0.5;
  }

  .wrap-colors-1,
  .wrap-colors-2 {
    overflow: hidden;
    border-radius: 100px;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    pointer-events: none;
  }
  .wrap-colors-1 {
    opacity: 0.35;
    filter: blur(3px);
  }
  .bg-colors {
    background: conic-gradient(
      transparent 0deg,
      #8400ff 65deg,
      #00ccff 144deg,
      #1356b4 180deg,
      transparent 324deg,
      transparent 360deg
    );
    position: absolute;
    width: 400px;
    height: 400px;
    margin: auto;
    inset: 0;
    left: 50%;
    transform: translateX(-50%) rotate(220deg);
    border-radius: 50%;
    animation: cycle-rotate 3s ease-in-out infinite;
  }
}
.output::before {
  content: "";
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  border-radius: inherit;
  position: absolute;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: #ffffff14;
  opacity: 0.4;
  transition:
    opacity 400ms linear,
    background-color 400ms linear;
}
.output::after {
  content: "";
  position: absolute;
  left: 12px;
  right: 12px;
  top: 14px;
  background: linear-gradient(0deg, transparent, black);
  height: 9px;
  mix-blend-mode: soft-light;
  border-radius: 100px;
}

@keyframes cycle-opacity {
  0% { opacity: 0; }
  30% { opacity: 1; }
  50% { opacity: 1; }
  100% { opacity: 0; }
}
@keyframes cycle-rotate {
  from { transform: translateX(-50%) rotate(0deg); }
  to { transform: translateX(-50%) rotate(360deg); }
}

.area {
  --ease-elastic: cubic-bezier(0.5, 2, 0.3, 0.8);

  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  inset: 0;

  .area-wrapper {
    &:hover .wrapper {
      transform: translateY(0) scale(1);
      box-shadow: 0 20px 50px -5px black;
    }
  }
}

.area::after {
  pointer-events: none;
  content: "";
  position: absolute;
  top: 66%;
  left: 0;
  right: 0;
  height: 100px;
  width: 30%;
  margin: auto;
  background-color: #648cc630;
  filter: blur(2em);
  opacity: 0.7;
  transform: perspective(10px) rotateX(5deg) scale(1, 0.5);
  z-index: 0;
}

.ticket-mask {
  position: absolute;
  overflow: hidden;
  display: flex;
  justify-content: center;
  mask-image: linear-gradient(rgba(0, 0, 0, 0.1) 0%, white 20px);
  perspective: 1000px;
  top: calc(50% - 142px);
  left: 0;
  right: 0;
  height: 100%;
  min-height: 1500px;
}

.ticket {
  float: left;
  animation: ticket-move 11s ease-in-out infinite;
  transform: translateY(50px);
  perspective: 3000px;
}

@keyframes ticket-move {
  0% {
    transform: translateY(-300px);
  }
  7% {
    transform: translateY(-250px);
  }
  12% {
    transform: translateY(-200px);
  }
  16% {
    transform: translateY(-170px);
  }
  22% {
    transform: translateY(-100px);
  }
  27% {
    transform: translateY(-40px);
  }
  34%,
  88% {
    transform: translateY(45px);
  }
  100% {
    transform: translateY(100%) rotateX(160deg) scale(5);
  }
}

.ticket:nth-child(2) .ticket-body {
  transition-delay: 0.7s;
}

.ticket:hover .ticket-flip-container {
  transform: rotateY(180deg);
}

.ticket-flip-container {
  transition: 0.6s;
  transform-style: preserve-3d;
  position: relative;
}

.float {
  transform-style: preserve-3d;
  pointer-events: none;
  animation: float 3s ease-in-out infinite;
}

.front,
.back {
  display: inline-block;
  backface-visibility: hidden;
  transform-style: preserve-3d;
}

.front {
  z-index: 1;
}

.back {
  position: absolute;
  top: 0;
  left: 0;
  transform: rotateY(-180deg);
}

@keyframes float {
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-15px);
  }
  100% {
    transform: translateY(0);
  }
}

.icon-cube {
  position: absolute;
  height: 110%;
  z-index: 1;
  top: -3px;
  left: 0;
  right: 0;
  margin: auto;
  mix-blend-mode: soft-light;
  opacity: 0.6;
}

.icon-cube path {
  animation-delay: calc(var(--i) * 100ms) !important;
  transform-origin: center;
}

.icon-cube .path-center {
  animation: path-center 3s ease-in-out infinite;
}
@keyframes path-center {
  50% {
    transform: scale(1.3);
  }
}

.icon-cube .path-t {
  animation: path-t 1.6s ease-in-out infinite;
}
@keyframes path-t {
  50% {
    transform: translateY(1px);
  }
}

.icon-cube .path-tl {
  animation: path-tl 1.6s ease-in-out infinite;
}
@keyframes path-tl {
  50% {
    transform: translateX(1px) translateY(1px);
  }
}

.icon-cube .path-tr {
  animation: path-tr 1.6s ease-in-out infinite;
}
@keyframes path-tr {
  50% {
    transform: translateX(-1px) translateY(1px);
  }
}

.icon-cube .path-br {
  animation: path-br 1.6s ease-in-out infinite;
}
@keyframes path-br {
  50% {
    transform: translateX(-1px) translateY(-1px);
  }
}

.icon-cube .path-bl {
  animation: path-bl 1.6s ease-in-out infinite;
}
@keyframes path-bl {
  50% {
    transform: translateX(1px) translateY(-1px);
  }
}

.icon-cube .path-b {
  animation: path-b 1.6s ease-in-out infinite;
}
@keyframes path-b {
  50% {
    transform: translateY(-1px);
  }
}

.ticket-body {
  display: block;
  position: relative;
  width: 320px;
  margin-bottom: 20px;
  padding: 0;
  border-radius: 7px 7px 0px 0px;
  background-color: white;
  text-align: center;
  background: linear-gradient(to bottom, white, #dcfffd);
  color: black;

  svg,
  img {
    pointer-events: none;
  }

  .bold {
    font-weight: 800;
  }

  header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: relative;
    padding: 15px;
    border-bottom: 1px dashed rgba(0, 0, 0, 0.4);
    text-align: left;
    height: 54px;

    .ticket-name {
      font-weight: 300;
      font-size: 1.05em;
      line-height: normal;
      align-items: center;
      display: flex;
      gap: 4px;
      letter-spacing: -2px;
    }

    span {
      display: inline-block;
    }

    time {
      display: flex;
    }

    .slash {
      padding: 0 1px;
      color: rgba(0, 0, 0, 0.4);
    }
  }
  header::after,
  header::before {
    content: "";
    display: block;
    width: 13px;
    height: 13px;
    background-color: #0f1114;
    position: absolute;
    right: -8px;
    border-radius: 50%;
    z-index: 11;
    bottom: -7px;
  }
  header:after {
    left: -8px;
  }
  .contents {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    min-height: 180px;
    position: relative;
    pointer-events: all;

    .event {
      display: flex;
      flex-direction: column;
      position: relative;
      z-index: 1;
      margin-top: -30px;
      font-weight: 600;

      span {
        display: inline-block;
        height: 15px;
        font-size: 3rem;
        font-weight: 400;
        line-height: 1;
      }
      span.bold {
        font-size: 2.18rem;
        font-weight: 800;
        margin-right: -3px;
      }

      div:nth-child(2) {
        font-size: 13px;
        letter-spacing: 0.45em;
        margin-left: 6px;
        color: #2f4c8b62;
      }
    }

    .number {
      position: absolute;
      left: 15px;
      bottom: -6px;
      font-size: 2em;
      color: #b5ddff;
      font-weight: bolder;
    }
  }
}
.ticket-body:after {
  content: "";
  display: block;
  position: absolute;
  bottom: -16px;
  left: 0;
  background:
    -webkit-linear-gradient(-135deg, #dcfffd 50%, transparent 50%) 0 50%,
    -webkit-linear-gradient(-45deg, #dcfffd 50%, transparent 50%) 0 50%,
    transparent;
  background-repeat: repeat-x;
  background-size:
    16px 16px,
    16px 16px,
    cover,
    cover;
  height: 16px;
  width: 100%;
  pointer-events: none;
}

.barcode {
