    37px 0 0 1px,
    41px 0 0 1px,
    44px 0 0 1px,
    47px 0 0 1px,
    51px 0 0 1px,
    56px 0 0 1px,
    59px 0 0 1px,
    64px 0 0 1px,
    68px 0 0 1px,
    72px 0 0 1px,
    74px 0 0 1px,
