.back header span {
  animation: none;
}
.ticket:hover .back header span {
  opacity: 0;
  animation: appear 0.5s var(--ease-elastic) forwards
    calc(var(--i) * 20ms + 400ms);
}

.ticket:hover .front header span {
  opacity: 1;
  animation: appear2;
}
.front header span {
  opacity: 0;
  animation: appear 0.5s var(--ease-elastic) forwards
    calc(var(--i) * 20ms + 400ms);
}

.qrcode {
  position: absolute;
  z-index: 1;
  color: #a5b7eb;

  img {
    display: block;
    height: 140px;
  }
}
.back .qrcode {
  margin-top: 8px;
}
.back .qrcode::after {
  --stroke-width: 0.2rem;
  --corner-size: 1rem;

  position: absolute;
  content: "";
  background:
    linear-gradient(
        to right,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      0 0,
    linear-gradient(
        to right,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      0 100%,
    linear-gradient(
        to left,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      100% 0,
    linear-gradient(
        to left,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      100% 100%,
    linear-gradient(
        to bottom,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      0 0,
    linear-gradient(
        to bottom,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      100% 0,
    linear-gradient(
        to top,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      0 100%,
    linear-gradient(
        to top,
        currentColor var(--stroke-width),
        transparent var(--stroke-width)
      )
      100% 100%;
  background-size: var(--corner-size) var(--corner-size);
  inset: 0;
  background-repeat: no-repeat;
}
.back .qrcode::after {
  animation: breath 3s var(--ease-elastic) infinite;
}

@keyframes breath {
  0% {
    transform: scale(1.05);
    opacity: 0.3;
  }
  50% {
    transform: scale(1.15);
    opacity: 1;
  }
  100% {
    transform: scale(1.05);
    opacity: 0.3;
  }
}

.front .qrcode {
  right: 5px;
  bottom: -5px;

  img {
