  height: 95px;
  will-change: transform;
  transform-origin: 50% 100%;
  transform: scale(0);
  filter: blur(10px);
  background: white;
  border-radius: 18px;
  box-shadow:
    0 0 0 2px #b0dbbf,
    1px 4px 5px 4px #ededed54;
  padding: 2px;
  animation-duration: 0.3s;
  animation-delay: 0.1s;
  animation-fill-mode: both;
  overflow: hidden;
}

#app:has(.hover-card:hover) > .reference > .card {
  transform: scale(1);
  animation-name: coolfadeIn;
}

.card:hover {
  pointer-events: all;
}

.bg-map {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: 0;
